export default function App() {
    return (
        <div className={tw`font-raleway text-center`}>
            <div className={tw`text-white mx-auto max-w-screen-md`}>
                <h1 className={tw`text-4xl font-regular mb-4`}>Hey there, I'm Olya</h1>
                <p className={tw`text-lg mb-3`}>currently in Istanbul</p>
                <p className={tw`text-lg mb-8`}>Find me on my socials ðŸŒ¸</p>
                <div className={tw`text-white flex flex-col`}>
                    <Link
                        icon={faGoogle}
