import React from "react";
import { css } from "@emotion/react";

export default function Header() {
  const headerContainerStyle = css`
    text-align: center;
    padding: 20px;

    h1 {
      margin-bottom: 10px;
    }

    /* Медиазапрос для мобильных устройств (например, ширина меньше 768px) */
    @media screen and (max-width: 767px) {
      p {
        display: block;
        margin-top: 10px; /* Пример: добавление отступа сверху для новой строки */
      }
    }
  `;

  return (
    <div css={headerContainerStyle}>
      <h1>Hey there, I'm Olya.</h1>
      <p>Find me on my socials</p>
    </div>
  );
}
